import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faUpload } from '@fortawesome/free-solid-svg-icons';
import './Todo.css';

import {
   useGetTodosQuery,
   useGetCommentsQuery,
   useAddTodoMutation,
   useUpdateTodoMutation,
   useDeleteTodoMutation
} from "../features/api/apiSlice"


const Comments = () => {
   const {
      data: comments,
      isLoading,
      isSuccess,
      isError,
      error
   } = useGetCommentsQuery()

   const [updateTodo] = useUpdateTodoMutation()
   const [deleteTodo] = useDeleteTodoMutation()

   function Content () {
      if (isLoading) return <p>Loading..</p>
      else if (isError) return <p>{error}</p>
      else if (isSuccess) return(
          comments.map((comment) => ( 
              <article key={comment.postId}>
                  <div>
                     <input
                        type="checkbox"
                        checked={comment.postId%2===0} //checked if postId is even number
                        id={comment.id}
                        onChange={() => updateTodo({ ...comment, completed: !comment.completed })}
                     />
                     <label htmlFor={comment.id}>{comment.name}</label>
                  </div>
                  <button className="trash" onClick={() => deleteTodo({ id: comment.id })}>
                <FontAwesomeIcon icon={faTrash} />
             </button>
              </article> 
           ))
      )
   }

   return (
      <Content />
   )
}

export default Comments
