//This file will contain the `Todo` component, 
//which will handle the display and management of the todo list.

//import:
//React
import React, { useState } from "react";
//the necessary hooks and components from `react-redux`
import { useSelector, useDispatch } from "react-redux";
//the actions from your `todoSlice`
import { addTodo, toggleComplete, deleteTodo } from "./todoSlice";
import './Todo.css';

const Todo = () => {
   const [text, setText] = useState("");
   const todos = useSelector((state) => state.todos);
   const dispatch = useDispatch();

   const handleInputChange = (e) => {
      setText(e.target.value);
   };

   const handleAddTodo = () => {
      if (text){
         dispatch(addTodo(text));
         setText("");
      }
   };

   const handleToggleComplete = (id) => {
      dispatch(toggleComplete(id));
   };

   const handleDeleteTodo = (id) => {
      dispatch(deleteTodo(id));
   };

   return(
      <div className="TodoDiv">
         <h1 className="header">To-Do List</h1>
         <input 
            className="input"
            type="text" 
            value={text} 
            placeholder="Enter a task" 
            onChange={handleInputChange}
         />
         {" "}
         <button 
            className="button"
            onClick={handleAddTodo}
         > 
            Add 
         </button>
         <ul className="list">
            {todos.map((todo) => (
               <li
                  className="bullet"
                  key={todo.id}
                  style={{
                     textDecoration: todo.completed ? "line-through" : "none",
                  }}
               >
                  {todo.text}
                  {" "}
                  <button 
                     className="button"
                     onClick={() => handleToggleComplete(todo.id)}
                  >
                     {todo.completed ? "Mark Incomplete" : "Mark Complete"}{" "}
                  </button>
                  {" "}
                  <button 
                     className="button"
                     onClick={() => handleDeleteTodo(todo.id)}
                  > 
                     Delete 
                  </button>
                  {" "}
               </li>
            ))}{" "}
         </ul>{" "}
      </div>
   );
};

export default Todo;
