//This file will contain the `Todo` component, 
//which will handle the display and management of the todo list.

//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
//import { faTrash, faUpload } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';
import './Todo.css';
import Comments from './comments';
import {
   useGetTodosQuery,
   useGetCommentsQuery,
   useAddTodoMutation,
   useUpdateTodoMutation,
   useDeleteTodoMutation
} from "../features/api/apiSlice"

const TodoList = () => {
   const [newText, setText] = useState('');
   const [allTodos, setTodos] = useState([]);

   const {
      data: posts,
      isLoading,
      isSuccess,
      isError,
      error
   } = useGetTodosQuery()

   const [addTodo] = useAddTodoMutation()
   const [updateTodo] = useUpdateTodoMutation()
   const [deleteTodo] = useDeleteTodoMutation()

   const handleInputChange = (e) => {
      e.preventDefault();
      //setText(e.target.value);
      console.log(newText);
      //addTodo({ userId: 1, title: newText, completed: false });
      //setText('');

   }



   function TodoContent () {
      if (isLoading) return <p>Loading...</p>
      else if (isError) return <p>{error}</p>
      else if (isSuccess) return(
         posts.map((todo) => ( 
              <article key={todo.id}>
                 <div>
                    <input
                          type="checkbox"
                          checked={todo.completed}
                          id={todo.id}
                          onChange={() => updateTodo({ ...todo, completed: !todo.completed })}
                    />
                    <label htmlFor={todo.id}>{todo.title}</label>
                 </div>
                 <button className="trash" onClick={() => deleteTodo({ id: todo.id })}> Delete </button>
              </article>
         ))
      )
   }

   return(
      <div className="TodoDiv">
         <h1 className="header">To-Do List</h1>
         <form onSubmit={handleInputChange}>
            <div className="new-text">
               <input 
                  //className="input"
                  type="text" 
                  id="new-text"
                  value={newText}
                  placeholder="Enter a new task" 
                  onChange={(e) => setText(e.target.value)}
                />
               {" "}
            </div>
            <button className="button"> Add </button>
         </form>

         <TodoContent /> 
         <Comments />


      </div>
   );
};

export default TodoList;
