//This file will contain the `Todo` component,
//which will handle the display and management of the todo list.

import { useState } from "react";
import "./app.css";
//import Comments from "./todos/comments";
import {
  useGetTodosQuery,
  //useGetCommentsQuery,
  useAddTodoMutation,
  useUpdateTodoMutation,
  useDeleteTodoMutation,
} from "./features/api/apiSlice";
import Todo from "./Todo";

const TodoList = () => {
  const [newText, setText] = useState("");
  //const [allTodos, setTodos] = useState([]);

  const {
    data: posts,
    isLoading,
    isSuccess,
    isError,
    error,
  } = useGetTodosQuery();

  // const [addTodo] = useAddTodoMutation();
  // const [updateTodo] = useUpdateTodoMutation();
  // const [deleteTodo] = useDeleteTodoMutation();

  // const handleInputChange = (e) => {
  //   e.preventDefault();
  //   //setText(e.target.value);
  //   console.log(newText);
  //   //addTodo({ userId: 1, title: newText, completed: false });
  //   //setText('');
  // };

  function TodoContent() {
    if (isLoading) return <p>Loading...</p>;
    else if (isError) return <p>{error}</p>;
    else if (isSuccess)
      return (
        <div className="container">
          {posts.map((todo) => (
            <div className="card" key={todo.id}>
              <h1 className="title">{todo.title}</h1>
              <hr></hr>
              <p className="body">{todo.body}</p>
            </div>
          ))}
        </div>
      );
  }

  return (
    <div>
      <Todo />
      <hr></hr>
      <div>
        <h1 className="header2">Flashcards from api</h1>
        <TodoContent />
      </div>
    </div>
  );
};

export default TodoList;
