import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const apiSlice = createApi({
   reducerPath: 'api', //default
   baseQuery: fetchBaseQuery({ baseUrl: 'https://jsonplaceholder.typicode.com' }),
   tagTypes: ['Todos', 'Comments'],
   endpoints: (builder) => ({
      getTodos: builder.query({
         query: () => '/todos',
         transformResponse: res => res.sort((a,b) => b.id - a.id),
         providesTags: ['Todos']
      }),
      //___.mutation --> actually changing data
      addTodo: builder.mutation({
         query: (todo) => ({
            url: '/todos',
            method: 'POST', //POST is used when updating the whole record
            body: todo
         }),
         invalidatesTags: ['Todos']
      }),
      updateTodo: builder.mutation({
         query: (todo) => ({
            url: `/todos/${todo.id}`,
            method: 'PATCH', //PATCH is used when updating part of the record
            body: todo
         }),
         invalidatesTags: ['Todos']
      }),
      deleteTodo: builder.mutation({
         query: ({ id }) => ({
            url: `/todos/${id}`,
            method: 'DELETE',
            body: id
         }),
         invalidatesTags: ['Todos']
      }),
      //to handle comments
      getComments: builder.query({
         query: () => '/comments',
         providesTags: ['Comments']
      }),
      updateComment: builder.mutation({
         query: (comment) => ({
            url: `/comments/${comment.id}`,
            method: 'PATCH', //PATCH is used when updating part of the record
            body: comment
         }),
         invalidatesTags: ['Comment']
      }),
      deleteComment: builder.mutation({
         query: ({ id }) => ({
            url: `/comments/${id}`,
            method: 'DELETE',
            body: id
         }),
         invalidatesTags: ['Comment']
      }),
   })
})

export const{
   useGetTodosQuery, //use_____Query format
   useGetCommentsQuery,
   useAddTodoMutation,
   useUpdateTodoMutation,
   useDeleteTodoMutation,
   useAddCommentMutation,
   useUpdateCommentMutation,
   useDeleteCommentMutation
} = apiSlice
