import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faUpload } from '@fortawesome/free-solid-svg-icons';
import './Todo.css';

import {
   useGetCommentsQuery,
   useCommentTodoMutation,
   useUpdateCommentMutation,
   useDeleteCommentoMutation
} from "../features/api/apiSlice"


const Comments = () => {
   const {
      data: comments,
      isLoading,
      isSuccess,
      isError,
      error
   } = useGetCommentsQuery()

   const [updateTodo] = useUpdateTodoMutation()
   const [deleteTodo] = useDeleteTodoMutation()

   function Content () {
      if (isLoading) return <p>Loading..</p>
      else if (isError) return <p>{error}</p>
      else if (isSuccess) return(
          comments.map((comment) => ( 
              <article key={comment.id}>
                  <div>
                     <input
                        type="checkbox"
                        checked={comment.id%2===0} //checked if id is even number
                        id={comment.id}
                        onChange={() => updateTodo({ ...comment, completed: !comment.completed })}
                     />
                     <label htmlFor={comment.id}>{comment.name}</label>
                  </div>
                  <button className="trash" onClick={() => deleteTodo({ id: comment.id })}> Delete </button>
              </article> 
           ))
      )
   }

   return (
      <Content />
   )
}

export default Comments
